package com.evilbird.warcraft.integration.device;

import com.evilbird.engine.game.GameInjector;
import dagger.Component;

import javax.inject.Singleton;

/**
 * Provides access to the dependency injected services that facilitate the game
 * engine. Implementation of this class will be generated by Dagger, binding
 * interfaces specified in the {@link GameInjector} interface to concrete
 * implementations as declared in {@link IntegrationModule}.
 *
 * @author Blair Butterworth
 */
@Component(modules = {IntegrationModule.class})
@Singleton
public interface IntegrationInjector extends GameInjector
{
}
